@import url("http://fonts.googleapis.com/css?family=Source+Sans+Pro:200,300,400,600,700,900,200italic,300italic,400italic,600italic,700italic,900italic");
/* normalize.css v3.0.0 | MIT License | git.io/normalize */
html {
  font-family: sans-serif;
  -ms-text-size-adjust: 100%;
  -webkit-text-size-adjust: 100%;
}
body {
  margin: 0;
}
article,
aside,
details,
figcaption,
figure,
footer,
header,
hgroup,
main,
nav,
section,
summary {
  display: block;
}
audio,
canvas,
progress,
video {
  display: inline-block;
  vertical-align: baseline;
}
audio:not([controls]) {
  display: none;
  height: 0;
}
[hidden],
template {
  display: none;
}
a {
  background: transparent;
}
a:active,
a:hover {
  outline: 0;
}
abbr[title] {
  border-bottom: 1px dotted;
}
b,
strong {
  font-weight: bold;
}
dfn {
  font-style: italic;
}
h1 {
  font-size: 2em;
  margin: 0.67em 0;
}
mark {
  background: #ff0;
  color: #000;
}
small {
  font-size: 80%;
}
sub,
sup {
  font-size: 75%;
  line-height: 0;
  position: relative;
  vertical-align: baseline;
}
sup {
  top: -0.5em;
}
sub {
  bottom: -0.25em;
}
img {
  border: 0;
}
svg:not(:root) {
  overflow: hidden;
}
figure {
  margin: 1em 40px;
}
hr {
  box-sizing: content-box;
  height: 0;
}
pre {
  overflow: auto;
}
code,
kbd,
pre,
samp {
  font-family: 'Bitstream Vera Sans Mono', Consolas, Courier, monospace, 'Bitstream Vera Sans Mono', Consolas, Courier, monospace;
  font-size: 1em;
}
button,
input,
optgroup,
select,
textarea {
  color: inherit;
  font: inherit;
  margin: 0;
}
button {
  overflow: visible;
}
button,
select {
  text-transform: none;
}
button,
html input[type="button"],
input[type="reset"],
input[type="submit"] {
  -webkit-appearance: button;
  cursor: pointer;
}
button[disabled],
html input[disabled] {
  cursor: default;
}
button::-moz-focus-inner,
input::-moz-focus-inner {
  border: 0;
  padding: 0;
}
input {
  line-height: normal;
}
input[type="checkbox"],
input[type="radio"] {
  box-sizing: border-box;
  padding: 0;
}
input[type="number"]::-webkit-inner-spin-button,
input[type="number"]::-webkit-outer-spin-button {
  height: auto;
}
input[type="search"] {
  -webkit-appearance: textfield;
  box-sizing: content-box;
}
input[type="search"]::-webkit-search-cancel-button,
input[type="search"]::-webkit-search-decoration {
  -webkit-appearance: none;
}
fieldset {
  border: 1px solid #c0c0c0;
  margin: 0 2px;
  padding: 0.35em 0.625em 0.75em;
}
legend {
  border: 0;
  padding: 0;
}
textarea {
  overflow: auto;
}
optgroup {
  font-weight: bold;
}
table {
  border-collapse: collapse;
  border-spacing: 0;
}
td,
th {
  padding: 0;
}
@font-face {
  font-family: 'gotham_blackregular';
  src: url("../fonts/ufonts.com_gotham_black-webfont.eot");
  src: url("../fonts/ufonts.com_gotham_black-webfont.eot?#iefix") format("embedded-opentype"), url("../fonts/ufonts.com_gotham_black-webfont.woff") format("woff"), url("../fonts/ufonts.com_gotham_black-webfont.ttf") format("truetype"), url("../fonts/ufonts.com_gotham_black-webfont.svg#gotham_blackregular") format("svg");
  font-weight: normal;
  font-style: normal;
}
::-moz-selection {
  background: #231f20;
  text-shadow: none;
  color: #fff;
}
::selection {
  background: #231f20;
  text-shadow: none;
  color: #fff;
}
body {
  line-height: 1.6em;
  font-family: 'Source Sans Pro', sans-serif;
  font-weight: 400;
  font-style: normal;
  color: #231f20;
}
header {
  position: relative;
  z-index: 6;
}
footer {
  position: relative;
  z-index: 4;
}
section {
  width: 100%;
  float: left;
}
section:nth-child(n+2) {
  margin: 40px 0 0 0;
}
* {
  outline: none;
}
.divider {
  height: 1px;
  border-top: 1px solid #b9babd;
  width: 100%;
  float: left;
}
.spacer {
  width: 100%;
  float: left;
  height: 0px;
}
.button {
  width: auto;
  padding: 0 10px;
  background: #a7a9ac;
  color: #fff;
  font-size: 12px;
  line-height: 26px;
  height: 26px;
  float: left;
  margin: 10px 0 0 0;
  text-decoration: none;
  border-top-left-radius: 4px;
  border-top-right-radius: 4px;
  border-bottom-right-radius: 4px;
  border-bottom-left-radius: 4px;
}
.button i {
  margin: 0 6px 0 0;
}
.button-primary {
  background: #25cbd3;
}
input {
  border-top-left-radius: 4px;
  border-top-right-radius: 4px;
  border-bottom-right-radius: 4px;
  border-bottom-left-radius: 4px;
  height: 32px;
  font-size: 12px;
  font-family: 'Source Sans Pro', sans-serif;
  font-weight: 400;
  font-style: normal;
}
input[type="text"],
input[type="email"] {
  border: 1px solid #e6e6e6;
  color: #231f20;
  padding: 0 10px;
}
input[type="submit"] {
  border: none;
  background: #231f20;
  color: #fff;
  text-transform: uppercase;
}
input[type="file"] {
  line-height: 0;
  padding: 7px 0 0 0;
}
input[type="radio"] {
  margin: 0;
  padding: 10px 0 0 0;
}
input[type="submit"].invite-submit {
  width: auto;
  padding: 0 10px;
  background: #231f20;
  color: #fff;
  font-size: 12px;
  line-height: 32px;
  height: 32px;
  float: left;
  margin: 0;
  text-decoration: none;
  border-top-left-radius: 4px;
  border-top-right-radius: 4px;
  border-bottom-right-radius: 4px;
  border-bottom-left-radius: 4px;
  font-family: 'gotham_blackregular', sans-serif;
  font-weight: normal !important;
}
textarea {
  border-top-left-radius: 4px;
  border-top-right-radius: 4px;
  border-bottom-right-radius: 4px;
  border-bottom-left-radius: 4px;
  font-size: 12px;
  font-family: 'Source Sans Pro', sans-serif;
  font-weight: 400;
  font-style: normal;
  border: 1px solid #e6e6e6;
  color: #231f20;
  resize: none;
  padding: 10px;
}
h1,
h2,
h3,
h4,
h5,
h6 {
  line-height: 1em;
  width: 100%;
  float: left;
}
h1 {
  font-family: 'Source Sans Pro', sans-serif;
  font-weight: 700;
  font-style: normal;
  font-size: 36px;
}
h2 {
  font-family: 'Source Sans Pro', sans-serif;
  font-weight: 600;
  font-style: normal;
  font-size: 20px;
}
h3 {
  font-family: 'Source Sans Pro', sans-serif;
  font-weight: 400;
  font-style: normal;
  font-size: 18px;
}
h4 {
  font-family: 'Source Sans Pro', sans-serif;
  font-weight: 700;
  font-style: normal;
  font-size: 16px;
}
h5 {
  font-family: 'Source Sans Pro', sans-serif;
  font-weight: 900;
  font-style: normal;
}
h6 {
  font-family: 'Source Sans Pro', sans-serif;
  font-weight: 900;
  font-style: normal;
}
.headCenter {
  text-align: center;
  line-height: 136%;
  margin: 0.1em 0;
}
h1.headCenter span.text {
  display: none;
}
h1.headCenter span.textLogo svg {
  width: 50px;
  height: 40px;
  position: relative;
  top: 8px;
  left: -10px;
  margin: 0 -38px 0 0;
}
h1.headCenter span.textLogo svg .logoI {
  fill: #231f20;
}
h1.headCenter span.textLogo svg .ioDot {
  fill: #25cbd3;
}
h1.headCenter span.textLogo svg .logoO {
  fill: #231f20;
}
h2.headCenter {
  font-family: 'Source Sans Pro', sans-serif;
  font-weight: 200;
  font-style: normal;
  color: #a7a9ac;
  letter-spacing: 0.02;
  margin: 0.1em 0 1.2em 0;
  font-size: 26px;
}
div.info-window h2 {
  margin-top: 0;
}
p {
  font-family: 'Source Sans Pro', sans-serif;
  font-weight: 200;
  font-style: normal;
  font-size: 18px;
  letter-spacing: 0.05em;
  color: #a7a9ac;
  margin: 1.4em 0;
}
p a {
  color: #25cbd3;
  text-decoration: none;
  border-bottom: 1px solid #25cbd3;
  font-family: 'Source Sans Pro', sans-serif;
  font-weight: 400;
  font-style: normal;
}
p a:hover {
  border-bottom: none;
}
p strong {
  font-family: 'Source Sans Pro', sans-serif;
  font-weight: 600;
  font-style: normal;
  color: #8b8e92;
}
p strong a {
  font-family: 'Source Sans Pro', sans-serif;
  font-weight: 600;
  font-style: normal;
}
blockquote {
  float: left;
  text-align: center;
  margin: 1em 80px;
  padding: 20px;
  font-size: 26px;
  line-height: 1.6em;
  color: #a7a9ac;
  font-weight: 700 !important;
  font-style: italic !important;
}
.two-colomn-text {
  width: 100%;
  float: left;
}
.two-colomn-text p {
  float: left;
}
.two-colomn-text p:nth-child(even) {
  margin: 1.4em 0 3em 0;
}
.the-content {
  width: 100%;
  padding: 120px 40px 160px 40px;
  float: left;
  position: relative;
  z-index: 5;
  box-sizing: border-box;
}
.the-content .the-row {
  max-width: 1180px;
  margin: 0 auto;
  clear: both;
}
.the-content .the-row .the-col-12,
.the-content .the-row .the-col-11,
.the-content .the-row .the-col-10,
.the-content .the-row .the-col-9,
.the-content .the-row .the-col-8,
.the-content .the-row .the-col-7,
.the-content .the-row .the-col-6,
.the-content .the-row .the-col-5,
.the-content .the-row .the-col-4,
.the-content .the-row .the-col-3,
.the-content .the-row .the-col-2,
.the-content .the-row .the-col-1 {
  width: 100%;
  float: left;
  margin: 0;
}
.the-content .the-row .fill-it {
  background: #25cbd3;
  color: #fff;
  text-align: center;
  border-right: 1px solid #24c3cb;
  border-bottom: 1px solid #24c3cb;
  box-sizing: border-box;
}
.the-content-what-top {
  padding-bottom: 0 !important;
}
.the-content-what-bottom {
  padding-top: 20px !important;
}
.the-content-full {
  padding: 60px 0 160px 0;
}
.the-content-full .the-row {
  max-width: none;
}
.the-content-full .the-row .the-col-12 .sidebar-left {
  width: 100%;
  float: left;
  background: #f5f5f5;
  padding: 20px 0;
  position: relative;
  z-index: 1;
  transition: all 0.4s ease;
  box-sizing: border-box;
}
.the-content-full .the-row .the-col-12 .main-right {
  width: 100%;
  float: left;
  padding: 20px 20px 40px 20px;
  position: relative;
  z-index: 0;
  transition: all 0.4s ease;
  box-sizing: border-box;
}
.the-content-full .the-row .the-col-12 .main-left {
  width: 100%;
  float: left;
  padding: 20px 20px 40px 20px;
  position: relative;
  z-index: 0;
  transition: all 0.4s ease;
  box-sizing: border-box;
}
.the-content-full .the-row .the-col-12 .sidebar-right {
  width: 100%;
  float: right;
  background: #f5f5f5;
  padding: 20px 0;
  position: relative;
  z-index: 1;
  transition: all 0.4s ease;
  box-sizing: border-box;
}
.the-content-full-2 {
  padding: 0 !important;
}
header {
  width: 100%;
  height: 60px;
  position: fixed;
  text-align: center;
  background: #231f20;
  padding: 0 40px;
  box-sizing: border-box;
}
header .headElements {
  width: 100%;
  max-width: 1180px;
  margin: 0 auto;
  position: relative;
}
header .head-full {
  max-width: none;
}
header .logoWrap {
  position: absolute;
  top: 15px;
  left: 0;
  z-index: 2;
}
header .logoWrap a {
  display: block;
  height: 30px;
  width: 30px;
}
header .logoWrap a img {
  width: 30px;
  position: relative;
  height: 30px;
  display: none;
}
header .svgWrap {
  position: absolute;
  top: 11px;
  left: -6px;
  z-index: 1;
}
header .svgWrap svg {
  width: 46px;
  position: relative;
  height: 46px;
}
header .svgWrap svg .logoI {
  fill: #fff;
}
header .svgWrap svg .ioDot {
  fill: #25cbd3;
}
header .svgWrap svg .logoO {
  fill: #fff;
}
header .svgWrap svg .slogan {
  fill: #a7a9ac;
}
header nav {
  position: absolute;
  width: 100%;
  z-index: 0;
  top: 0;
  left: 0;
}
header nav .expander {
  float: right;
  width: auto;
  margin: 0;
}
header nav .expander a {
  color: #fff;
  display: block;
  float: left;
  padding: 0 0 0 20px;
}
header nav .expander a i {
  float: left;
  line-height: 60px;
  font-size: 30px;
  display: none;
}
header nav .expander a img {
  width: 30px;
  height: auto;
  float: left;
  margin-top: 20px;
}
header nav .social-but-wrap {
  float: right;
  position: relative;
  top: 0px;
  right: 0;
  display: none;
}
header nav .social-but-wrap dl dt {
  margin: 0 4px;
  float: left;
}
header nav .social-but-wrap dl dt a {
  color: #fff;
  font-size: 12px;
}
header nav .social-but-wrap dl dt a span i.fa-stack-2x {
  color: #fff;
}
header nav .social-but-wrap dl dt a span i.fa-stack-1x {
  color: #231f20;
}
header nav .social-but-wrap dl dt a:hover {
  font-size: 12px;
}
header nav ul {
  display: none;
  width: 100%;
  margin: 0;
  list-style: none;
  position: fixed;
  left: 0;
  top: 60px;
  background: #231f20;
  padding: 0;
  box-sizing: border-box;
}
header nav ul li {
  width: 100%;
  height: auto;
  float: left;
  position: relative;
  text-transform: uppercase;
  text-align: center;
  line-height: 60px;
  font-family: 'gotham_blackregular', sans-serif;
  font-weight: normal !important;
}
header nav ul li a {
  text-decoration: none;
  color: #fff;
  display: block;
  height: 100%;
  width: 100%;
  font-size: 36px;
}
header nav ul li a:hover {
  font-size: 40px;
}
header.light {
  background: none;
}
header.light nav .expander {
  position: relative;
  right: -40px;
}
header.light nav .expander a {
  color: #231f20;
  background: #fff;
  padding-right: 40px;
  padding-left: 40px;
}
header.light nav .social-but-wrap dl dt a {
  color: #231f20;
}
header.light nav .social-but-wrap dl dt a span i.fa-stack-2x {
  color: #231f20;
}
header.light nav .social-but-wrap dl dt a span i.fa-stack-1x {
  color: #fff;
}
header.light nav ul {
  background: none;
}
header.light nav ul li a {
  color: #231f20;
}
header.light.headerActive {
  background: #fff;
}
header.light.headerActive nav ul {
  background: #fff;
}
header.launch svg .logoI {
  fill: #231f20;
}
header.launch svg .ioDot {
  fill: #25cbd3;
}
header.launch svg .logoO {
  fill: #231f20;
}
div.homeLogo {
  width: 100%;
  height: 1000px;
  position: relative;
  text-align: center;
  z-index: 1;
}
div.homeLogo .bigLogoWrap,
div.homeLogo .bigSvgWrap {
  width: 100%;
  height: auto;
  position: absolute;
  text-align: center;
  top: -400px;
  opacity: 0;
}
div.homeLogo .bigLogoWrap:before,
div.homeLogo .bigSvgWrap:before {
  content: '';
  display: inline-block;
  height: 100%;
  vertical-align: middle;
  margin: 0 0 0 -0.25em;
}
div.homeLogo img {
  width: 200px;
  cursor: pointer;
  display: inline-block;
  vertical-align: middle;
  position: relative;
  transition: all 0.4s ease;
}
div.homeLogo svg {
  width: 200px;
  cursor: pointer;
  display: inline-block;
  vertical-align: middle;
  position: relative;
  transition: all 0.4s ease;
}
div.homeLogo svg .logoI {
  fill: #231f20;
}
div.homeLogo svg .ioDot {
  fill: #25cbd3;
}
div.homeLogo svg .logoO {
  fill: #231f20;
}
div.homeLogo svg .slogan {
  fill: #a7a9ac;
}
.the-sketch-wrap {
  width: 100%;
  float: left;
  height: auto;
}
.the-sketch-wrap .the-sketch svg,
.the-sketch-wrap .the-sketch-2 svg {
  width: 100%;
  height: auto;
  max-height: 620px;
}
.the-sketch-wrap .the-sketch img,
.the-sketch-wrap .the-sketch-2 img {
  width: 100%;
  height: auto;
  max-height: 620px;
}
.welcome-content {
  padding-top: 60px !important;
}
ul.callActions {
  margin: 0;
  padding: 0;
  list-style: none;
  text-align: center;
}
ul.callActions li {
  display: inline-block;
  margin: 20px;
}
ul.callActions li a {
  width: 180px;
  height: 60px;
  color: #fff;
  font-size: 16px;
  line-height: 60px;
  text-transform: uppercase;
  display: block;
  background: #231f20;
  text-align: center;
  text-decoration: none;
  font-family: 'gotham_blackregular', sans-serif;
  font-weight: normal !important;
  border-top-left-radius: 4px;
  border-top-right-radius: 4px;
  border-bottom-right-radius: 4px;
  border-bottom-left-radius: 4px;
  transition: all 0.4s ease;
  -webkit-backface-visibility: hidden;
  -ms-backface-visibility: hidden;
  backface-visibility: hidden;
}
ul.ourClients {
  width: 100%;
  float: left;
  margin: 0;
  padding: 0;
  list-style: none;
  text-align: center;
}
ul.ourClients li {
  display: inline-block;
  margin: 10px;
  opacity: 0.4;
}
ul.ourClients li img {
  width: 80%;
  height: auto;
}
.topGraphic {
  width: 100%;
  float: left;
  max-height: 600px;
  overflow: hidden;
}
.topGraphic img {
  width: 100%;
  height: auto;
  display: block;
}
.graphic-top-content {
  padding-top: 60px !important;
}
ul.team {
  margin: 40px 0 0 0;
  float: left;
  padding: 0;
  list-style: none;
}
ul.team li {
  height: auto;
  float: left;
  width: 100%;
  margin: 0 1% 40px 1%;
  position: relative;
  padding-bottom: 20px !important;
  border-bottom: 10px solid #25cbd3;
  -webkit-animation: BORDER-SWOP 100s infinite;
  -ms-animation: BORDER-SWOP 100s infinite;
  animation: BORDER-SWOP 100s infinite;
}
ul.team li .memberImage {
  position: relative;
}
ul.team li .memberImage:before {
  content: '';
  display: block;
  position: absolute;
  width: 100%;
  height: 100%;
  box-shadow: inset 0 -5px 3px 0 rgba(34,34,34,0.3);
}
ul.team li .memberImage img {
  display: block;
  width: 100%;
}
ul.team li .memberImage .color-bio-photo {
  position: absolute;
  top: 0;
  opacity: 0;
  transition: all 0.2s ease-in;
}
ul.team li:hover .color-bio-photo {
  opacity: 1;
}
ul.team li .memberDetail {
  text-align: center;
  padding: 2em;
}
ul.team li .memberDetail h4 {
  margin: 10px 0 10px 0;
  font-family: 'gotham_blackregular', sans-serif;
  font-weight: normal !important;
  float: left;
}
ul.team li .memberDetail h5 {
  margin: 8px 0 0 0;
  font-family: 'gotham_blackregular', sans-serif;
  font-weight: normal !important;
  float: left;
  color: #25cbd3;
}
ul.team li .memberDetail p {
  margin-top: 10px;
  font-size: 14px;
  float: left;
  color: #84878b;
  font-weight: 400;
}
ul.team li ul.memberSocial {
  float: left;
  list-style: none;
  text-align: center;
  margin: -6px 0 0 0;
  padding: 0;
  width: 100%;
}
ul.team li ul.memberSocial li {
  float: none;
  display: inline;
  margin: 0 1% !important;
}
ul.team li ul.memberSocial li.memberTwitter a i.fa-stack-2x {
  color: #3cf;
}
ul.team li ul.memberSocial li.memberLinkedIn a i.fa-stack-2x {
  color: #0077b5;
}
ul.team li ul.memberSocial li.memberGitHub a i.fa-stack-2x {
  color: #333;
}
ul.team li ul.memberSocial li.memberMedium a i.fa-stack-2x {
  color: #57ad68;
}
ul.team li ul.memberSocial li.memberPinterest a i.fa-stack-2x {
